import { REDUX_SET_USERDATA } from "../actions/actionTypes";


const INITIAL_STATE = {
  userData: {  },
  auth:"",
  lifesycle:true
};
export const UserReducer = (state = INITIAL_STATE, action) => {

  switch (action.type) {
    case REDUX_SET_USERDATA:
      console.log('reducer works in case',action.data)
        localStorage.setItem('token',JSON.stringify(action.data.auth))
        localStorage.setItem('user',JSON.stringify(action.data.user))
        localStorage.setItem('lifesycle',true)
        console.log(localStorage.getItem('user'))
        return { userData : action.data.user, auth:action.data.auth,lifesycle:action.data.lifesycle }
    default:
      return state;
  }
};
