import React from "react";
import { Link, useLocation } from "react-router-dom";

const Navi = () => {
  const location = useLocation();
  return (
    <div className="w-11/12 h-20 flex items-center text-[#707070] space-x-2 font-exo lg:w-full   md:w-full ">
      <div className="w-4/12 lg:w-3/12 lg:justify-end sm:w-full centered-items">
        <Link to="/">
          <span className="orange font-bold text-2xl font-sans  ">
            Apply4Job
          </span>
        </Link>
      </div>

      <div
        className={`w-4/12 xl:w-5/12 lg:w-8/12  lg:centered-items md:w-full   sm:hidden flex justify-base ${
          (location.pathname === "/login") || (location.pathname === "/signup")
            ? "hidden" : "visible"
           
        }`}
      >
        <ul className="flex space-x-10 sm:space-x-1 font-bold">
          <li className="hover:cursor-pointer hover:text-blue-400">Find Job</li>
          <li className="hover:cursor-pointer hover:text-blue-400">
            Company Application
          </li>
          <li className="hover:cursor-pointer hover:text-blue-400">
            Company Reviews
          </li>
        </ul>
      </div>

      <div
        className={`w-4/12 md:w-4/12 lg:w-2/12 mt-2  sm:hidden h-10 space-x-5 centered-items ${
          (location.pathname === "/login") || (location.pathname === "/signup")
            ? "hidden" : "visible"
            
        }  `}
      >
        <Link to="/signup">
          <span className="font-bold border-r h-full centered-items pr-4 hover:orange hover:cursor-pointer">
            Sign Up
          </span>
        </Link>
        <Link to="/login">
          <span className="font-bold text-blue-400 hover:orange hover:cursor-pointer">
            Login
          </span>
        </Link>
      </div>
    </div>
  );
};

export default Navi;
