import { toast } from "react-toastify";
export function tostMessageCheck(
  data = { body: String, isSuccess: Boolean, time: Number }
) {
  const { body = "Bilinmeyen değer.", isSuccess = true, time = 1000 } = data;
  toast(body, {
    autoClose: time,
    type: isSuccess ? "success" : "error",
    theme: "colored",
  });
}
