  const [firstText, setFirstText] = useState();
  const [secondText, setSecondText] = useState("");
  document.onmouseup = () => {
    setSelectedText(window.getSelection().toString());
  };
  function split(str, index) {
    const result = [str.slice(0, index), str.slice(index)];

    return result;
  }
  const changeCss = (type) => {
     div = document.getElementById("container").textContent;

    console.log(div.indexOf(selectedText));
    let [firstText2, secondText2] = split(div, div.indexOf(selectedText));
    setFirstText(firstText2);
    //setSecondText(secondText2);
    setSecondText(React.createElement('h5', {}, secondText2))
    
  };
  return (
    <div>
      <button onClick={() => changeCss("h1")} className="border-2">
        h1
      </button>
      <h1>Hi I'am Tiptap</h1>
      <div className="hello" name="container" id="container">
        let div
      </div><br/>
      <div>
        {div}
      </div>
    </div>
  );
};

export default Mulalakt1;
