import React, { useState } from 'react';

const HamburgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="hamburger-menu relative">
      <div
        className={`hamburger-icon w-6 h-6 flex flex-col justify-between cursor-pointer ${isOpen ? 'open' : ''}`}
        onClick={handleMenuToggle}
      >
        <span className="hamburger-line w-full h-1 bg-black transition-all duration-300"></span>
        <span className="hamburger-line w-full h-1 bg-black transition-all duration-300"></span>
        <span className="hamburger-line w-full h-1 bg-black transition-all duration-300"></span>
      </div>
      <ul className={`menu-items absolute top-0 left-0 mt-8 ml-2 ${isOpen ? 'flex flex-col' : 'hidden'}`}>
        <li className="menu-item py-2">Menu Item 1</li>
        <li className="menu-item py-2">Menu Item 2</li>
        <li className="menu-item py-2">Menu Item 3</li>
      </ul>
    </div>
  );
};

export default HamburgerMenu;
