import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { userLogout } from "../redux/actions/UserLogout";
import { useNavigate } from "react-router-dom";

const HamburgerMenu = () => {
  const dispatch = useDispatch;
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const searchDivAbility = useRef();
  const searchDivLocation = useRef();

  const handleMenuToggle = () => {
    setIsOpen(!isOpen);
  };
  const logOut = () => {
    dispatch(userLogout());
    navigate("/");
  };
  return (
    <div
      className={`hamburger-menu  -mt-5  w-full  p-2 h-10 float-right items-center justify-end flex ${
        isOpen ? "open" : ""
      }`}
    >
      <ul
        className={`menu-items  justify-center  items-center w-full space-x-[2%] ${
          isOpen ? "flex" : "hidden"
        }`}
      >
        <li className="w-3/12  flex justify-end ">            <div
              ref={searchDivAbility}
              className=" h-fit p-3 md:p-1 md:w-4/12 space-x-2 border-b-2 centered-items  border-orange-400 bg-[#F9F9F9] rounded"
            >
              <span className="h-full  centered-items">
                <Searchlogo />
              </span>
              <input 
                onFocus={() =>
                  {
                    searchDivAbility.current.classList.remove("border-orange-400")
                    searchDivAbility.current.classList.add("border-blue-400")
                  }
                  
                }
                onBlur={() =>
                  {
                    searchDivAbility.current.classList.remove("border-blue-400")
                  searchDivAbility.current.classList.add("border-orange-400")
                  }
                  
                }
                placeholder="Ãœnvan veya yetenek"
                className="h-full md:text-sm md:w-[130px] focus:outline-none bg-[#F9F9F9]    "
              />
            </div></li>
        <li className="w-3/12 flex justify-end ">Menu Item 2</li>
        <li className="w-3/12 flex justify-end ">Menu Item 3</li>
        <span
            onClick={() => logOut()}
            className="font-bold flex w-2/12  text-gray-500  text-sm font-exo justify-end items-center   md:text-sm  h-10   hover:orange hover:cursor-pointer"
          >
            Log out
          </span>
      </ul>
      <div
        className="hamburger-icon   flex flex-col justify-between w-4 h-4 cursor-pointer"
        onClick={handleMenuToggle}
      >
        <span
          className={`w-full h-0.5 bg-orange-400 transition-transform  transition-opacity origin-top-left duration-300 ${
            isOpen ? "rotate-45 translate-y-1.5" : ""
          }`}
        ></span>
        <span
          className={`w-full h-0.5 bg-orange-400 transition-opacity duration-300 ${
            isOpen ? "opacity-0" : ""
          }`}
        ></span>
        <span
          className={`w-full h-0.5 bg-orange-400 transition-transform transition-opacity origin-bottom-left duration-300 ${
            isOpen ? "-rotate-45 -translate-y-2" : ""
          }`}
        ></span>
      </div>
    </div>
  );
};

export default HamburgerMenu;
