import React, { useState } from 'react';

const HamburgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`hamburger-menu -mt-5 border w-full space-x-10 p-2 h-10 float-right items-center justify-end flex ${isOpen ? 'open' : ''}`}>
   <ul className={`menu-items justify-center w-full border ${isOpen ? 'flex' : 'hidden'}`}>
        <li className="py-2">Menu Item 1</li>
        <li className="py-2">Menu Item 2</li>
        <li className="py-2">Menu Item 3</li>
      </ul>
      <div
        className="hamburger-icon   flex flex-col justify-between w-4 h-4 cursor-pointer"
        onClick={handleMenuToggle}
      >
        <span
          className={`w-full h-0.5 bg-orange-400 transition-transform  transition-opacity origin-top-left duration-300 ${
            isOpen ? 'rotate-45 translate-y-1.5' : ''
          }`}
        ></span>
        <span
          className={`w-full h-0.5 bg-orange-400 transition-opacity duration-300 ${isOpen ? 'opacity-0' : ''}`}
        ></span>
        <span
          className={`w-full h-0.5 bg-orange-400 transition-transform transition-opacity origin-bottom-left duration-300 ${
            isOpen ? '-rotate-45 -translate-y-2' : ''
          }`}
        ></span>
      </div>
      
    </div>
  );
};

export default HamburgerMenu;
