import { REDUX_SET_USERDATA } from "../actions/actionTypes";
import { delay, put } from "redux-saga/effects";
import { tostMessageCheck } from "./ToastMessageCheck";
import history from "../../history";


export function* userLogin({ payload }) {
  console.log(payload);
  const { email, password } = payload;
  let data = yield fetch("http://localhost:5000/login", {
    method: "post",
    body: JSON.stringify({ email, password }),
    headers: {
      "Content-Type": "application/json",
    },
  });
  data = yield data.json();
  console.warn("saga data", data);
  if (data.result) {
    yield put({ type: REDUX_SET_USERDATA, data });
    tostMessageCheck({ body: data.body, isSuccess: true });
    yield delay(2000)
    history.push("/")
    history.go();
    
  } else {
    tostMessageCheck({ body: data.body, isSuccess: false });
  }
}

export function* userCheck() {
  let result = yield fetch("http://localhost:5000/check", {
    method: "post",
    headers: {
      authorization: `bearer ${JSON.parse(localStorage.getItem("token"))}`,
    },
  });
  result = yield result.json();
  console.warn("sonuc", result);
  if (result.result) {
    console.log("true dondu");
    let user = JSON.parse(localStorage.getItem("user"));
    let auth = JSON.parse(localStorage.getItem("token"));
    let data = { user, auth, lifesycle: true };
    console.warn("data is here ", data);
    yield put({ type: REDUX_SET_USERDATA, data });
  } 
}

export function* userSignUp({ data }) {
  console.warn("userSignUp,aasdasda",data)
  const { email, password, ipAddress } = data;
  let result =  yield fetch("http://localhost:5000/signup", {
      method: "POST",
      body: JSON.stringify({ email, password, ipAddress }),
      headers: {
        "Content-Type": "application/json",
      },
    });
  result = yield result.json();
  console.warn("saga result ", result);
  if (result.result) {
    yield put({ type: REDUX_SET_USERDATA, data });
    console.warn('result true döndü')
    tostMessageCheck({ body: result.body, isSuccess: true });
    yield delay(2000)
    // history.push("/")
    // history.go();
  } else {
    tostMessageCheck({ body: result.body, isSuccess: false });
  }
}
