import { toast } from "react-toastify";
export function* tostMessageCheck({ data }) {
    
  const [body="Bilinmeyen değer.", result=false, time=1000] = data;
  toast(body,{
    autoClose:time,
    type: result ? "success":"error",
    theme:"colored"
  })
}
