import React, { useState } from 'react';

const HamburgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`hamburger-menu border p-2 h-10 items-center flex ${isOpen ? 'open' : ''}`}>
      <div
        className="hamburger-icon flex flex-col justify-between w-4 h-4 cursor-pointer"
        onClick={handleMenuToggle}
      >
        <span
          className={`w-full h-0.5 bg-black transition-transform transition-opacity origin-top-left duration-300 ${
            isOpen ? 'rotate-45 translate-y-1.5' : ''
          }`}
        ></span>
        <span
          className={`w-full h-0.5 bg-black transition-opacity duration-300 ${isOpen ? 'opacity-0' : ''}`}
        ></span>
        <span
          className={`w-full h-0.5 bg-black transition-transform transition-opacity origin-bottom-left duration-300 ${
            isOpen ? '-rotate-45 -translate-y-2' : ''
          }`}
        ></span>
      </div>
      <ul className={`menu-items ${isOpen ? 'flex' : 'hidden'}`}>
        <li className="py-2">Menu Item 1</li>
        <li className="py-2">Menu Item 2</li>
        <li className="py-2">Menu Item 3</li>
      </ul>
    </div>
  );
};

export default HamburgerMenu;
