import { REDUX_SET_USERDATA } from "../actions/actionTypes";
import { put } from "redux-saga/effects";

export  function* UserLoginSaga({payload})
{    
     console.log(payload)
     const {email,password} = payload;
     let data = yield fetch("http://localhost:5000/login",{
          method: "post",
          body: JSON.stringify({email,password}),
          headers: {
            "Content-Type": "application/json",
          },
     });
     data = yield data.json();
     yield put({type:REDUX_SET_USERDATA,data});
     
}

export function* userCheckSaga({payload})
{
     console.log("saga ici usercheck",payload)
     let result = yield fetch("http://localhost:5000/check",{
          method:"post",
          headers:{
               authorization:`bearer ${JSON.parse(localStorage.getItem('token'))}`
          }
     })
   
     console.warn('sonuc', result.redirected)
     if(result) {
          console.WARN('saga ici data2', data)
          let data = localStorage.getItem('user')
          data = yield data.json();
          
          yield put({type:REDUX_SET_USERDATA, data})
     }
     else{
          console.warn('BASARISIZ')
     }
     
}
