import React, { useEffect, useRef, useState } from 'react';

const Animation = ({ animationData }) => {
  const canvasRef = useRef(null);
  const [isAnimationRunning, setIsAnimationRunning] = useState(true);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Animasyonu burada oluşturun

    const animationLoop = () => {
      if (!isAnimationRunning) return;

      // Animasyon çizdirme işlemleri

      requestAnimationFrame(animationLoop);
    };

    animationLoop();

    return () => {
      // Animasyonu durdurmak veya temizlemek için gerekli işlemleri burada yapın
    };
  }, [animationData, isAnimationRunning]);

  const handleCanvasClick = () => {
    setIsAnimationRunning(!isAnimationRunning);
  };

  return <canvas ref={canvasRef} width={animationData.w} height={animationData.h} onClick={handleCanvasClick} />;
};

export default Animation;
